const API_URL = "http://localhost:5000/api/missions";

// Fetch all missions
export const getMissions = async () => {
    const response = await fetch(API_URL);
    return response.json();
};

// Create a new mission
export const createMission = async (mission) => {
  console.log("Sending Mission Data:", mission); // Debugging log

  try {
      const response = await fetch("http://localhost:5000/api/missions", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(mission),
      });

      const data = await response.json();
      console.log("API Response:", data); // Debugging log

      if (!response.ok) {
          throw new Error(data.message || "Failed to create mission");
      }
  } catch (error) {
      console.error("Error creating mission:", error);
      alert(error.message);
  }
};


// Update mission status
export const updateMissionStatus = async (id, status) => {
    await fetch(`${API_URL}/${id}/status`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status }),
    });
};
