import React, { useState } from "react";
import { createMission } from "../services/missionService";

const MissionForm = ({ onMissionAdded }) => {
    const [form, setForm] = useState({ name: "", startLat: "", startLng: "", destLat: "", destLng: "" });

    const handleSubmit = async (e) => {
      e.preventDefault();

      // Convert lat/lng to numbers before sending
      const formattedMission = {
          ...form,
          startLat: parseFloat(form.startLat),
          startLng: parseFloat(form.startLng),
          destLat: parseFloat(form.destLat),
          destLng: parseFloat(form.destLng),
      };

      await createMission(formattedMission);
      setForm({ name: "", startLat: "", startLng: "", destLat: "", destLng: "" });
      onMissionAdded();
  };


    return (
        <form onSubmit={handleSubmit} className="mt-3">
            <input type="text" placeholder="Mission Name" className="form-control mb-2" onChange={(e) => setForm({ ...form, name: e.target.value })} required />
            <input type="text" placeholder="Start Latitude" className="form-control mb-2" onChange={(e) => setForm({ ...form, startLat: e.target.value })} required />
            <input type="text" placeholder="Start Longitude" className="form-control mb-2" onChange={(e) => setForm({ ...form, startLng: e.target.value })} required />
            <input type="text" placeholder="Destination Latitude" className="form-control mb-2" onChange={(e) => setForm({ ...form, destLat: e.target.value })} required />
            <input type="text" placeholder="Destination Longitude" className="form-control mb-2" onChange={(e) => setForm({ ...form, destLng: e.target.value })} required />
            <button type="submit" className="btn btn-primary">Create Mission</button>
        </form>
    );
};

export default MissionForm;
