import React, { useState } from "react";
import { createMission } from "../services/missionService";

const MissionForm = ({ onMissionAdded }) => {
  const [form, setForm] = useState({
    name: "",
    startLat: "",
    startLng: "",
    destLat: "",
    destLng: ""
  });

  const convertToDecimal = (coord, direction) => {
    let value = parseFloat(coord);
    if (direction === "S" || direction === "W") value = -value; // South & West are negative
    return value;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formattedMission = {
      ...form,
      startLat: convertToDecimal(form.startLat, form.startDir),
      startLng: convertToDecimal(form.startLng, form.startLngDir),
      destLat: convertToDecimal(form.destLat, form.destDir),
      destLng: convertToDecimal(form.destLng, form.destLngDir)
    };

    await createMission(formattedMission);
    setForm({ name: "", startLat: "", startLng: "", destLat: "", destLng: "" });
    onMissionAdded();
  };

  return (
    <form onSubmit={handleSubmit} className="mt-3">
      <input
        type="text"
        placeholder="Mission Name"
        className="form-control mb-2"
        onChange={(e) => setForm({ ...form, name: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Start Latitude"
        className="form-control mb-2"
        onChange={(e) => setForm({ ...form, startLat: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Start Longitude"
        className="form-control mb-2"
        onChange={(e) => setForm({ ...form, startLng: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Destination Latitude"
        className="form-control mb-2"
        onChange={(e) => setForm({ ...form, destLat: e.target.value })}
        required
      />
      <input
        type="text"
        placeholder="Destination Longitude"
        className="form-control mb-2"
        onChange={(e) => setForm({ ...form, destLng: e.target.value })}
        required
      />
      <button type="submit" className="btn btn-primary">
        Create Mission
      </button>
    </form>
  );
};

export default MissionForm;
