import React, { useEffect, useState } from "react";
import { getMissions, updateMissionStatus } from "../services/missionService";
import { io } from "socket.io-client";
import MissionForm from "../components/MissionForm";
import MapDisplay from "../components/MapDisplay";

const socket = io("http://localhost:5000"); // Connect to WebSocket

const MissionPlanner = () => {
    const [missions, setMissions] = useState([]);

    useEffect(() => {
        fetchMissions();

        // Listen for real-time mission updates
        socket.on("missionAdded", (newMission) => {
            setMissions((prevMissions) => [...prevMissions, newMission]);
        });

        socket.on("missionUpdated", (updatedMission) => {
            setMissions((prevMissions) =>
                prevMissions.map((mission) =>
                    mission._id === updatedMission._id ? updatedMission : mission
                )
            );
        });

        return () => {
            socket.off("missionAdded");
            socket.off("missionUpdated");
        };
    }, []);

    const fetchMissions = async () => {
        const data = await getMissions();
        setMissions(data);
    };

    const handleStatusUpdate = async (id, newStatus) => {
        await updateMissionStatus(id, newStatus);
    };

    return (
        <div className="container mt-4">
            <h2>Mission Planner</h2>
            <MissionForm onMissionAdded={fetchMissions} />
            <MapDisplay missions={missions} />

            <ul className="list-group mt-3">
                {missions.map((mission) => (
                    <li key={mission._id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                            <strong>{mission.name}</strong> - {mission.startLat}, {mission.startLng} ‚ûù {mission.destLat}, {mission.destLng}
                            <span className={`badge bg-${mission.status === "Completed" ? "success" : mission.status === "In Progress" ? "warning" : "secondary"} ms-2`}>
                                {mission.status}
                            </span>
                        </div>
                        <div>
                            {mission.status === "Pending" && (
                                <button className="btn btn-warning btn-sm me-2" onClick={() => handleStatusUpdate(mission._id, "In Progress")}>Start</button>
                            )}
                            {mission.status === "In Progress" && (
                                <button className="btn btn-success btn-sm" onClick={() => handleStatusUpdate(mission._id, "Completed")}>Complete</button>
                            )}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MissionPlanner;
