import React, { useEffect, useState } from "react";
import { getMissions } from "../services/missionService";
import MissionForm from "../components/MissionForm";

const MissionPlanner = () => {
    const [missions, setMissions] = useState([]); // Ensure it's an array

    useEffect(() => {
        fetchMissions();
    }, []);

    const fetchMissions = async () => {
        try {
            const data = await getMissions();
            if (Array.isArray(data)) {
                setMissions(data); // ✅ Set only if it's an array
            } else {
                setMissions([]); // Fallback to an empty array
                console.error("Unexpected API response:", data);
            }
        } catch (error) {
            console.error("Error fetching missions:", error);
            setMissions([]); // Avoid breaking .map()
        }
    };

    return (
        <div className="container mt-4">
            <h2>Mission Planner</h2>
            <MissionForm onMissionAdded={fetchMissions} />
            {missions.length > 0 ? (
                <ul className="list-group mt-3">
                    {missions.map((mission) => (
                        <li key={mission._id} className="list-group-item">
                            <strong>{mission.name}</strong> - {mission.startLocation} ➝ {mission.destination}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No missions available.</p>
            )}
        </div>
    );
};

export default MissionPlanner;
