import React, { useState } from "react";
import { createMission } from "../services/missionService";

const MissionForm = ({ onMissionAdded }) => {
    const [form, setForm] = useState({ name: "", startLocation: "", destination: "" });

    const handleSubmit = async (e) => {
        e.preventDefault();
        await createMission(form);
        setForm({ name: "", startLocation: "", destination: "" });
        onMissionAdded(); // Refresh mission list
    };

    return (
        <form onSubmit={handleSubmit} className="mt-3">
            <input
                type="text"
                placeholder="Mission Name"
                value={form.name}
                className="form-control mb-2"
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                required
            />
            <input
                type="text"
                placeholder="Start Location"
                value={form.startLocation}
                className="form-control mb-2"
                onChange={(e) => setForm({ ...form, startLocation: e.target.value })}
                required
            />
            <input
                type="text"
                placeholder="Destination"
                value={form.destination}
                className="form-control mb-2"
                onChange={(e) => setForm({ ...form, destination: e.target.value })}
                required
            />
            <button type="submit" className="btn btn-primary">Create Mission</button>
        </form>
    );
};

export default MissionForm;
